import './App.css';
import { ApolloClient, InMemoryCache, ApolloProvider, useQuery, gql } from '@apollo/client';
import { format } from 'date-fns';

const client = new ApolloClient({
  uri: 'https://api.spacex.land/graphql/',
  cache: new InMemoryCache(),
});

const GET_LAUNCHES_PAST = gql`
  query {
    launchesPast(limit: 6) {
      mission_name
      launch_date_local
      launch_site {
        site_name
      }
      links {
        article_link
        video_link
      }
    }
  }
`;

const LaunchesPast = () => {
  const { loading, error, data } = useQuery(GET_LAUNCHES_PAST);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  return (
    <div className="container">
      {data.launchesPast.map((launch) => (
        <div className="card">
          <div className="wrapper">
            <h3>{launch.mission_name}</h3>
            <p>Date: {format(new Date(launch.launch_date_local), 'yyyy/MM/dd')}</p>
            <p>Site: {launch.launch_site.site_name_long}</p>
            <ul>
              <h4>Links</h4>
              {launch.links.article_link && (
                <li>
                  <a href={launch.links.article_link}>Article</a>
                </li>
              )}
              {launch.links.video_link && (
                <li>
                  <a href={launch.links.video_link}>Video</a>
                </li>
              )}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
};

const GET_LAUNCHES_PAST_FAIL = gql`
  query {
    launchesPast(find: { launch_success: "true" }) {
      mission_name
      launch_date_local
      launch_site {
        site_name_long
      }
      links {
        article_link
        video_link
      }
    }
  }
`;

const LaunchesPastFail = () => {
  const { loading, error, data } = useQuery(GET_LAUNCHES_PAST_FAIL);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  return (
    <div className="container">
      {data.launchesPast.map((launch) => (
        <div className="card">
          <div className="wrapper">
            <h3>{launch.mission_name}</h3>
            <p>Date: {format(new Date(launch.launch_date_local), 'yyyy/MM/dd')}</p>
            <p>Site: {launch.launch_site.site_name_long}</p>
            <ul>
              <h4>Links</h4>
              {launch.links.article_link && (
                <li>
                  <a href={launch.links.article_link}>Article</a>
                </li>
              )}
              {launch.links.video_link && (
                <li>
                  <a href={launch.links.video_link}>Video</a>
                </li>
              )}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
};

const App = () => {
  return (
    <div className="wrapper">
      <ApolloProvider client={client}>
        <h2>Space X 🚀</h2>
        <LaunchesPast />
        <LaunchesPastFail />
      </ApolloProvider>
    </div>
  );
};

export default App;
