import './App.css';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from '@apollo/client';

const client = new ApolloClient({
  uri: 'http://graphql.org/swapi-graphql',
  cache: new InMemoryCache(),
});

const GET_FILMS = gql`
  query starwars {
    films(first: $first) {
      id
      name
      image
      types
      maxHP
      maxCP
      attacks {
        special {
          name
          damage
        }
      }
    }
  }
`;

const Films = () => {
  const { loading, error, data } = useQuery(GET_FILMS, {
    variables: { first: 151 },
  });

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  return (
    <div>
      {data.films.map((film) => (
        <div key={film.name} value={film.name}>
          <h2>{film.name}</h2>
          <img src={film.image} alt="" />
          {film.types.map((type) => (
            <p>{type}</p>
          ))}
        </div>
      ))}
    </div>
  );
};

const App = () => {
  return (
    <ApolloProvider client={client}>
      <h2>My first Apollo app ðŸš€</h2>
      <Films />
    </ApolloProvider>
  );
};

export default App;
