import './App.css';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from '@apollo/client';

const client = new ApolloClient({
  uri: 'https://ghibliql.herokuapp.com/',
  cache: new InMemoryCache(),
});

const GET_FILMS = gql`
  query GetCountries {
    films {
      title
      director
      people {
        name
      }
    }
  }
`;

function Films({ onDogSelected }) {
  const { loading, error, data } = useQuery(GET_FILMS);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  return (
    <select name="film" onChange={onDogSelected}>
      {data.films.map((film) => (
        <option key={film.name} value={film.name}>
          {film.name}
        </option>
      ))}
    </select>
  );
}

function App() {
  return (
    <ApolloProvider client={client}>
      <h2>My first Apollo app ðŸš€</h2>
      <Films />
    </ApolloProvider>
  );
}

export default App;
