import './App.css';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from '@apollo/client';

const client = new ApolloClient({
  uri: 'https://graphql.anilist.co',
  cache: new InMemoryCache(),
});

const GET_FILMS = gql`
  query NirvanaAlbumSingles {
    lookup {
      artist(mbid: "5b11f4ce-a62d-471e-81fc-a69a8278c7da") {
        name
        releaseGroups(type: ALBUM) {
          edges {
            node {
              title
              firstReleaseDate
              relationships {
                releaseGroups(type: "single from") {
                  edges {
                    node {
                      target {
                        ... on ReleaseGroup {
                          title
                          firstReleaseDate
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
`;

const Films = (id) => {
  const { loading, error, data } = useQuery(GET_FILMS, { variables: { id } });

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  return <div>{data.Media.title.romaji}</div>;
};

const App = () => {
  return (
    <ApolloProvider client={client}>
      <h2>My first Apollo app ðŸš€</h2>
      <Films id={1} />
    </ApolloProvider>
  );
};

export default App;
