import './App.css';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
  createHttpLink,
} from '@apollo/client';

const link = createHttpLink({
  uri: 'http://graphql.org/swapi-graphql',
  fetchOptions: {
    mode: 'no-cors',
  },
});

const client = new ApolloClient({
  cache: new InMemoryCache(),
  link,
});

const GET_FILMS = gql`
  query GetAllData {
    allPeople {
      people {
        id
        name
        gender
        birthYear
        eyeColor
        hairColor
        height
        mass
        skinColor
        created
        edited
      }
    }
  }
`;

const Films = () => {
  const { loading, error, data } = useQuery(GET_FILMS);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  return <div>{data}</div>;
};

const App = () => {
  return (
    <ApolloProvider client={client}>
      <h2>My first Apollo app ðŸš€</h2>
      <Films />
    </ApolloProvider>
  );
};

export default App;
