import './App.css';
import { ApolloClient, InMemoryCache, ApolloProvider, useQuery, gql } from '@apollo/client';
import { format } from 'date-fns';

const client = new ApolloClient({
  uri: 'https://api.spacex.land/graphql/',
  cache: new InMemoryCache(),
});

const GET_LAUNCHES_PAST = gql`
  query {
    launchesPast(limit: 6) {
      mission_name
      launch_date_local
      launch_site {
        site_name_long
      }
      links {
        article_link
        video_link
        flickr_images
      }
    }
  }
`;

const LaunchesPast = () => {
  const { loading, error, data } = useQuery(GET_LAUNCHES_PAST);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  return (
    <div className="container">
      {data.launchesPast.map((launch) => (
        <div className="card">
          <div className="wrapper">
            <h3>{launch.mission_name}</h3>
            <div className="img-container">
              {launch.links.flickr_images.length > 0 ? (
                <img
                  src={launch.links.flickr_images[Math.floor(Math.random() * launch.links.flickr_images.length)]}
                  alt=""
                />
              ) : (
                <img src={null} alt="Not Found" />
              )}
            </div>
            <p>Date: {format(new Date(launch.launch_date_local), 'yyyy/MM/dd')}</p>
            <p>Site: {launch.launch_site.site_name_long}</p>
            <p>
              Links:&nbsp
              {launch.links.article_link && <a href={launch.links.article_link}>Article</a>},&nbsp
              {launch.links.video_link && <a href={launch.links.video_link}>Video</a>}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

const App = () => {
  return (
    <div className="wrapper">
      <ApolloProvider client={client}>
        <h2>Space X ðŸš€</h2>
        <LaunchesPast />
      </ApolloProvider>
    </div>
  );
};

export default App;
